import React from "react";
import { about } from "../../data/about";
import { stack } from "../../data/stack";
import { emojis } from "../../data/emojis";

const emoji = emojis[Math.floor(Math.random() * emojis.length)];

export const About = () => {
  return (
    <div className="md:px-8 flex mt-20 xl:px-20">
      <div className="about-text md:w-5/12 xl:w-1/2">
        <p className="text-xl xl:my-10">{about.currentWork}</p>
        <p className="text-xl xl:my-10">{about.skills}</p>
        <p className="text-xl md:my-3 xl:my-5">{about.currentStack}</p>
        <ul className="ml-3 mb-10">
          {stack.map((s) => (
            <li className="xl:flex">- {s}</li>
          ))}
        </ul>
        <p className="text-xl  md:my-3 xl:my-10 ">{about.currentProjects}</p>
        <p className="text-xl md:my-3 xl:my-10">
          {about.hobbies}&nbsp;{emoji}
        </p>
      </div>
      <div className="md:w-7/12 md:mt-32 md:mr-12 xl:w-1/2 flex justify-end">
        <img
          src={`assets/${about.image}`}
          alt="cats in bag"
          className="profile-image rounded-3xl"
        />
      </div>
    </div>
  );
};
