import React from "react";
import { work } from "../../data/work";

export const Work = () => {
  return (
    <div className="md:px-8 mt-32 pb-10 xl:px-20">
      <h1 className="text-6xl font-bold md:tracking-tighter ">My Work</h1>
      <div className="mt-32">
        {work.map((w) => (
          <div className="my-16 flex">
            <div className="flex flex-col md:w-1/3 xl:w-1/3 xl:justify-center">
              <div className="my-10">
                <h1 className="text-4xl font-extrabold">{w.name}</h1>
              </div>
              <p className="description-text my-5 text-xl">{w.description}</p>
              <p className="description-text xl:my-5 xl:text-xl">
                {w.additional}
              </p>
              <div className="xl:my-10">
                <a href={w.link} className="xl:flex xl:items-center">
                  <span className="secondary-text-color xl:text-2xl xl:font-extrabold">
                    {w.name}
                    &nbsp;
                    <i class="fas fa-long-arrow-alt-right xl:relative xl:top-1"></i>
                  </span>
                </a>
              </div>
            </div>
            <div className="xl:w-2/3 xl:flex xl:justify-center xl:relative xl:left-20 xl:top-20">
              <img
                src={`assets/${w.image}`}
                alt="screenshot"
                className="work-image xl:rounded-3xl"
              />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};
